}
.create-account-question::before {
	color: black!important;
}

.wd-sub-menu li>a {
    position: relative;
    display: flex;
    align-items: center;
    padding-top: 8px;
    padding-bottom: 8px;
    color: #000000;
    font-size: 14px;
    line-height: 1.3;
}
body.single-product .livrare {
    color: #ffffff;
    font-size: 16px;
    font-weight: 500;
    background-color: black;
    padding: 5px;
	 text-align:center;
	 display: inline-flex;
    margin-bottom: 10px;
	  margin-top: 10px;
	  margin-right: 14px;
    margin-left: 14px;
}
}
@media only screen and (max-width: 993px) {
body.single-product .livrare {
    font-size: 14px;
	}}
@media only screen and (min-width: 993px) {
form.woocommerce-checkout {
    flex-wrap: inherit!important;
	}}

